targets.expr:
sum by(provisioner, workerType) (
  avg_over_time(tc_queue2_prometheus_running_workers{
    provisioner!~"^gecko.*",
    provisioner!~".*k8s",
    provisioner=~"$provisioner", 
    workerType=~"$workerType",
  }[$__interval])
)

sum by(provisioner, workerType) (
  avg_over_time(tc_queue2_prometheus_workers_total{
    provisioner!~"^gecko.*",
    provisioner!~".*k8s",
    provisioner=~"$provisioner", 
    workerType=~"$workerType", 
  }[$__interval])
  - 
  avg_over_time(tc_queue2_prometheus_quarantined_workers{
    provisioner!~"^gecko.*",
    provisioner!~".*k8s",
    provisioner=~"$provisioner", 
    workerType=~"$workerType", 
  }[$__interval])
)

sum by(provisioner, workerType) (
  avg_over_time(tc_queue2_prometheus_quarantined_workers{
    provisioner!~"^gecko.*",
    provisioner!~"k8s",
    provisioner=~"$provisioner", 
    workerType=~"$workerType", 
  }[$__interval])
)
